update class_attr_property_cnfg set PROPERTY_VALUE='CASE WHEN IS_HEADER_ROW = ''Y'' THEN CASE WHEN NOM_STATUS IN (''EQM_NOM_CNEQ_SENT'', ''EQM_NOM_CANCELLED'') THEN ''false;'' ELSE ''true;'' END ELSE ''false;'' END || CASE WHEN IS_HEADER_ROW = ''N'' THEN ''viewtype=label'' END' where class_name='CT_TRNP_DAY_EXC_NOM' and attribute_name='CEQ_PERIOD_START_DT' and PROPERTY_CODE='vieweditable';
update class_attr_property_cnfg set PROPERTY_VALUE='CASE WHEN NOM_STATUS = ''EQM_NOM_PRELIMINARY'' AND IS_HEADER_ROW = ''Y'' THEN ''true;'' ELSE ''false;'' END || CASE WHEN IS_HEADER_ROW = ''N'' THEN ''viewtype=label'' END' where class_name='CT_TRNP_DAY_EXC_NOM' and attribute_name='EXC_LIFT_CARGO_VOL' and PROPERTY_CODE='vieweditable' ;
update class_attr_property_cnfg set PROPERTY_VALUE='CASE WHEN NOM_STATUS = ue_ct_cpp_eqm.getConstStatusPrelim OR (ec_contract_attribute.attribute_string(ue_ct_cpp_util.getRefCntrId,''CPP_NQM_EQM_PORTAL_DOWN'', DAYTIME, ''<='') = ''Y'' AND NOM_STATUS != ue_ct_cpp_eqm.getConstStatusCEQSent) THEN ''true;'' ELSE ''false;'' END' where class_name='CT_TRNP_DAY_EXC_NOM' and attribute_name='UPG_VOL_NQ' and PROPERTY_CODE='vieweditable';