CREATE OR REPLACE FORCE EDITIONABLE VIEW "V_CONT_JOURNAL_SUMMARY" ("OBJECT_ID", "COMPANY_ID", "FISCAL_YEAR", "DAYTIME", "PERIOD", "DOCUMENT_KEY", "LIST_ITEM_KEY", "SUMMARY_SETUP_ID", "DATASET", "FIN_ACCOUNT_ID", "FIN_COST_CENTER_ID", "FIN_COST_OBJECT_ID", "FIN_REVENUE_ORDER_ID", "FIN_WBS_ID", "DEBIT_CREDIT", "ACTUAL_AMOUNT", "AMOUNT_ADJUSTMENT", "ACTUAL_QTY_1", "QTY_ADJUSTMENT", "FORECAST_AMOUNT", "FORECAST_QTY_1", "AMEND_AMOUNT", "AMEND_QTY_1", "UOM1_CODE", "CURRENCY_ID", "TAG", "LABEL", "COMMENTS", "TEXT_1", "TEXT_2", "TEXT_3", "TEXT_4", "TEXT_5", "TEXT_6", "TEXT_7", "TEXT_8", "TEXT_9", "TEXT_10", "VALUE_1", "VALUE_2", "VALUE_3", "VALUE_4", "VALUE_5", "DATE_1", "DATE_2", "DATE_3", "DATE_4", "DATE_5", "REF_OBJECT_ID_1", "REF_OBJECT_ID_2", "REF_OBJECT_ID_3", "REF_OBJECT_ID_4", "REF_OBJECT_ID_5", "UPDATE_CLASS_AMOUNT", "UPDATE_REF_ID_AMOUNT", "UPDATE_CLASS_QTY", "UPDATE_REF_ID_QTY", "PRE_UPDATE_AMOUNT", "PRE_UPDATE_QTY1", "RECORD_STATUS", "CREATED_BY", "CREATED_DATE", "LAST_UPDATED_BY", "LAST_UPDATED_DATE", "REV_NO", "REV_TEXT", "APPROVAL_BY", "APPROVAL_DATE", "APPROVAL_STATE", "REC_ID", "SORT_ORDER") AS 
  SELECT
      s."OBJECT_ID",s."COMPANY_ID",s."FISCAL_YEAR",s."DAYTIME",s."PERIOD",s."DOCUMENT_KEY",s."LIST_ITEM_KEY",s."SUMMARY_SETUP_ID",s."DATASET",s."FIN_ACCOUNT_ID",s."FIN_COST_CENTER_ID",s."FIN_COST_OBJECT_ID",s."FIN_REVENUE_ORDER_ID",s."FIN_WBS_ID",s."DEBIT_CREDIT",s."ACTUAL_AMOUNT",s."AMOUNT_ADJUSTMENT",s."ACTUAL_QTY_1",s."QTY_ADJUSTMENT",s."FORECAST_AMOUNT",s."FORECAST_QTY_1",s."AMEND_AMOUNT",s."AMEND_QTY_1",s."UOM1_CODE",s."CURRENCY_ID",s."TAG",s."LABEL",s."COMMENTS",s."TEXT_1",s."TEXT_2",s."TEXT_3",s."TEXT_4",s."TEXT_5",s."TEXT_6",s."TEXT_7",s."TEXT_8",s."TEXT_9",s."TEXT_10",s."VALUE_1",s."VALUE_2",s."VALUE_3",s."VALUE_4",s."VALUE_5",s."DATE_1",s."DATE_2",s."DATE_3",s."DATE_4",s."DATE_5",s."REF_OBJECT_ID_1",s."REF_OBJECT_ID_2",s."REF_OBJECT_ID_3",s."REF_OBJECT_ID_4",s."REF_OBJECT_ID_5",s."UPDATE_CLASS_AMOUNT",s."UPDATE_REF_ID_AMOUNT",s."UPDATE_CLASS_QTY",s."UPDATE_REF_ID_QTY",s."PRE_UPDATE_AMOUNT",s."PRE_UPDATE_QTY1",s."RECORD_STATUS",s."CREATED_BY",s."CREATED_DATE",s."LAST_UPDATED_BY",s."LAST_UPDATED_DATE",s."REV_NO",s."REV_TEXT",s."APPROVAL_BY",s."APPROVAL_DATE",s."APPROVAL_STATE",s."REC_ID",
--      l.object_id summary_setup_id,
      l.sort_order SORT_ORDER
FROM
       CONT_JOURNAL_SUMMARY       s,
       SUMMARY_SETUP_LIST         l
WHERE
        s.fin_account_id = l.fin_account_id
  AND   (  s.fin_wbs_id           = l.fin_wbs_id
       OR  s.fin_cost_center_id   = l.fin_cost_center_id
       OR  s.fin_revenue_order_id = l.fin_revenue_order_id)