CREATE OR REPLACE FORCE EDITIONABLE VIEW "V_FIN_DOC_EX_RATE_MAINTAIN" ("OBJECT_ID", "TRANS_SORT_ORDER", "DOCUMENT_KEY", "TRANSACTION_KEY", "TRANSACTION_NAME", "FOREX_VARIANT_PRICE_TO_BOOK", "CURR_FROM_TO_PRICE_TO_BOOK", "FOREX_DATE_PRICE_TO_BOOK", "FOREX_RATE_PRICE_TO_BOOK", "CURR_FROM_TO_ID_PRICE_TO_BOOK", "INV_FOREX_RATE_PRICE_TO_BOOK", "FOREX_SOURCE_PRICE_TO_BOOK", "TIME_SCOPE_PRICE_TO_BOOK", "DATE_BASE_CODE_PRICE_TO_BOOK", "FOREX_VARIANT_PRICE_TO_MEMO", "CURR_FROM_TO_PRICE_TO_MEMO", "FOREX_DATE_PRICE_TO_MEMO", "FOREX_RATE_PRICE_TO_MEMO", "CURR_FROM_TO_ID_PRICE_TO_MEMO", "INV_FOREX_RATE_PRICE_TO_MEMO", "FOREX_SOURCE_PRICE_TO_MEMO", "TIME_SCOPE_PRICE_TO_MEMO", "DATE_BASE_CODE_PRICE_TO_MEMO", "FOREX_VARIANT_BOOK_TO_GROUP", "CURR_FROM_TO_BOOK_TO_GROUP", "FOREX_DATE_BOOK_TO_GROUP", "FOREX_RATE_BOOK_TO_GROUP", "CURR_FROM_TO_ID_BOOK_TO_GROUP", "INV_FOREX_RATE_BOOK_TO_GROUP", "FOREX_SOURCE_BOOK_TO_GROUP", "TIME_SCOPE_BOOK_TO_GROUP", "DATE_BASE_CODE_BOOK_TO_GROUP", "FOREX_VARIANT_BOOK_TO_LOCAL", "CURR_FROM_TO_BOOK_TO_LOCAL", "FOREX_DATE_BOOK_TO_LOCAL", "FOREX_RATE_BOOK_TO_LOCAL", "CURR_FROM_TO_ID_BOOK_TO_LOCAL", "INV_FOREX_RATE_BOOK_TO_LOCAL", "FOREX_SOURCE_BOOK_TO_LOCAL", "TIME_SCOPE_BOOK_TO_LOCAL", "DATE_BASE_CODE_BOOK_TO_LOCAL", "RECORD_STATUS", "CREATED_BY", "CREATED_DATE", "LAST_UPDATED_BY", "LAST_UPDATED_DATE", "REV_NO", "REV_TEXT") AS 
  select c.object_id                                                                  as OBJECT_ID,
       c.sort_order                                                                 as TRANS_SORT_ORDER,
       c.document_key                                                               as DOCUMENT_KEY,
       c.TRANSACTION_KEY                                                            as TRANSACTION_KEY,
       c.NAME                                                                       as TRANSACTION_NAME,
	   'PRICING_TO_BOOKING'                                                         as FOREX_VARIANT_PRICE_TO_BOOK,
       c.pricing_booking_label_1                                                    as CURR_FROM_TO_PRICE_TO_BOOK,
       c.EX_PRICING_BOOKING_DATE                                                    as FOREX_DATE_PRICE_TO_BOOK,
       c.EX_PRICING_BOOKING                                                         as FOREX_RATE_PRICE_TO_BOOK,
       c.pricing_booking_label_2                                                    as CURR_FROM_TO_ID_PRICE_TO_BOOK,
       c.EX_INV_PRICING_BOOKING                                                     as INV_FOREX_RATE_PRICE_TO_BOOK,
       ec_forex_source_version.name(c.EX_PRICING_BOOKING_ID, daytime, '<=')         as FOREX_SOURCE_PRICE_TO_BOOK,
       EC_PROSTY_CODES.code_text(c.ex_pricing_booking_ts, 'FX_TYPE')                as TIME_SCOPE_PRICE_TO_BOOK,
       EC_PROSTY_CODES.code_text(c.EX_PRICING_BOOKING_DBC, 'TRANS_FOREX_BASE_CODE') as DATE_BASE_CODE_PRICE_TO_BOOK,
       'PRICING_TO_MEMO'                                                            as FOREX_VARIANT_PRICE_TO_MEMO,
       c.pricing_memo_label_1                                                       as CURR_FROM_TO_PRICE_TO_MEMO,
       c.EX_PRICING_MEMO_DATE                                                       as FOREX_DATE_PRICE_TO_MEMO,
       c.EX_PRICING_MEMO                                                            as FOREX_RATE_PRICE_TO_MEMO,
       c.pricing_memo_label_2                                                       as CURR_FROM_TO_ID_PRICE_TO_MEMO,
       c.EX_INV_PRICING_MEMO                                                        as INV_FOREX_RATE_PRICE_TO_MEMO,
       ec_forex_source_version.name(c.EX_PRICING_MEMO_ID, daytime, '<=')            as FOREX_SOURCE_PRICE_TO_MEMO,
       EC_PROSTY_CODES.code_text(c.EX_PRICING_MEMO_TS, 'FX_TYPE')                   as TIME_SCOPE_PRICE_TO_MEMO,
       EC_PROSTY_CODES.code_text(c.EX_PRICING_MEMO_DBC, 'TRANS_FOREX_BASE_CODE')    as DATE_BASE_CODE_PRICE_TO_MEMO,
       'BOOKING_TO_GROUP'                                                           as FOREX_VARIANT_BOOK_TO_GROUP,
       c.booking_group_label_1                                                      as CURR_FROM_TO_BOOK_TO_GROUP,
       c.EX_BOOKING_GROUP_DATE                                                      as FOREX_DATE_BOOK_TO_GROUP,
       c.EX_BOOKING_GROUP                                                           as FOREX_RATE_BOOK_TO_GROUP,
       c.booking_GROUP_label_2                                                      as CURR_FROM_TO_ID_BOOK_TO_GROUP,
       c.EX_INV_BOOKING_GROUP                                                       as INV_FOREX_RATE_BOOK_TO_GROUP,
       ec_forex_source_version.name(c.EX_BOOKING_GROUP_ID, daytime, '<=')           as FOREX_SOURCE_BOOK_TO_GROUP,
       EC_PROSTY_CODES.code_text(c.EX_BOOKING_GROUP_TS, 'FX_TYPE')                  as TIME_SCOPE_BOOK_TO_GROUP,
       EC_PROSTY_CODES.code_text(c.EX_BOOKING_GROUP_DBC, 'TRANS_FOREX_BASE_CODE')   as DATE_BASE_CODE_BOOK_TO_GROUP,
       'BOOKING_TO_LOCAL'                                                           as FOREX_VARIANT_BOOK_TO_LOCAL,
       c.booking_local_label_1                                                      as CURR_FROM_TO_BOOK_TO_LOCAL,
       c.EX_BOOKING_LOCAL_DATE                                                      as FOREX_DATE_BOOK_TO_LOCAL,
       c.EX_BOOKING_LOCAL                                                           as FOREX_RATE_BOOK_TO_LOCAL,
       c.booking_local_label_2                                                      as CURR_FROM_TO_ID_BOOK_TO_LOCAL,
       c.EX_INV_BOOKING_LOCAL                                                       as INV_FOREX_RATE_BOOK_TO_LOCAL,
       ec_forex_source_version.name(c.EX_BOOKING_LOCAL_ID, daytime, '<=')           as FOREX_SOURCE_BOOK_TO_LOCAL,
       EC_PROSTY_CODES.code_text(c.EX_BOOKING_LOCAL_TS, 'FX_TYPE')                  as TIME_SCOPE_BOOK_TO_LOCAL,
       EC_PROSTY_CODES.code_text(c.EX_BOOKING_LOCAL_DBC, 'TRANS_FOREX_BASE_CODE')   as DATE_BASE_CODE_BOOK_TO_LOCAL,
       c.record_status                                                              as RECORD_STATUS,
       c.created_by                                                                 as CREATED_BY,
       c.created_date                                                               as CREATED_DATE,
       c.last_updated_by                                                            as LAST_UPDATED_BY,
       c.last_updated_date                                                          as LAST_UPDATED_DATE,
       c.rev_no                                                                     as REV_NO,
       c.rev_text                                                                   as REV_TEXT
  from cont_transaction c